{{--  Button Sidebar : en cas de Responsive --------------------------------------------------- --}}
<div class="fixed right-4 bottom-4 z-30 lg:hidden">
  <button id="sidebarToggle" 
          class="flex justify-center items-center w-12 h-12 text-white bg-gradient-to-b from-gray-950 to-black rounded-full shadow-lg transition-all duration-300 ">
    <i class="fas fa-bars"></i>
  </button>
</div>

<script>
  document.addEventListener('DOMContentLoaded', function() {
    const sidebarToggle = document.getElementById('sidebarToggle');
    const sidebar = document.getElementById('sidebar');
    const overlay = document.getElementById('overlay');
    const mainContent = document.querySelector('.main-content');
    
    sidebarToggle.addEventListener('click', function() {
      sidebar.classList.toggle('open');
      sidebar.classList.toggle('-translate-x-full');
      sidebar.classList.toggle('translate-x-0');
      overlay.classList.toggle('hidden');
    });
    
    overlay.addEventListener('click', function() {
      sidebar.classList.remove('open');
      sidebar.classList.remove('translate-x-0');
      sidebar.classList.add('-translate-x-full');
      overlay.classList.add('hidden');
    });

    function adjustLayout() {
      if (window.innerWidth >= 1024) {
        sidebar.classList.remove('-translate-x-full');
        sidebar.classList.add('translate-x-0');
        overlay.classList.add('hidden');
        if (mainContent) {
          mainContent.style.width = `calc(100% - ${sidebar.offsetWidth}px)`;
        }
      } else {
        if (!sidebar.classList.contains('open')) {
          sidebar.classList.add('-translate-x-full');
          sidebar.classList.remove('translate-x-0');
        }
        if (mainContent) {
          mainContent.style.width = '100%';
        }
      }
    }
    
    adjustLayout();
    window.addEventListener('resize', adjustLayout);
    
    document.addEventListener('click', function(event) {
      if (window.innerWidth < 1024 && 
          !sidebar.contains(event.target) && 
          !sidebarToggle.contains(event.target) &&
          sidebar.classList.contains('translate-x-0')) {
          
          sidebar.classList.remove('open');
          sidebar.classList.remove('translate-x-0');
          sidebar.classList.add('-translate-x-full');
          overlay.classList.add('hidden');
      }
    });
  });
</script>